import {Component, Input} from '@angular/core';
import {ChatModel, DallImage, ImageList} from "../../models";
import {UserRole} from "../../models/chat.model";
import {NzNotificationService} from "ng-zorro-antd/notification";
import {DomSanitizer} from "@angular/platform-browser";

import {GPTType} from "../../models/GPTType";

@Component({
  selector: 'app-dialogue',
  templateUrl: './dialogue.component.html',
  styleUrl: './dialogue.component.css'
})
export class DialogueComponent {
  constructor(private notification: NzNotificationService,
              private sanitizer: DomSanitizer,) {
  }
  fallback =
    'data:image/png;base64,';

  private _chatModel: ChatModel | undefined;
  private _content: string | undefined;
  public imageList: ImageList | undefined;
  @Input()
  set content(value: string | undefined) {
    this._content = value;
    this.segments();
  }

  @Input()
  set chatModel(value: ChatModel | undefined) {
    this._chatModel = value;
    this.segments();
  }

  get chatModel(): ChatModel | undefined {
    return this._chatModel;
  }

  protected readonly UserRole = UserRole;
  separatedSegments: { isCode: boolean; content: string; }[] | undefined;
  get type(): GPTType | undefined{
    return this._chatModel?.type===undefined?undefined: this._chatModel?.type;
  }
  segments() {
    if (!this.chatModel) return;
    if(this.type===GPTType.Image){
      this.segmentsImage();
    }else{
      this.segmentsChat();
    }
  }
  segmentsImage(){
    try{
      this.imageList = {
        dataId: this.chatModel?.dataId!,
        images: JSON.parse(this.chatModel?.content!) as DallImage[]
      }
    }catch (e){

    }
  }
  segmentsChat(){
    if (!this.chatModel) return;
    const segments: { content: string, isCode: boolean }[] = [];
    const regex = /```(.*?)```/gs; // 匹配 ``` ``` 代码块
    let match;
    let lastIndex = 0;
    while ((match = regex.exec(this.chatModel.content)) !== null) {
      const codeStartIndex = match.index;
      const codeEndIndex = regex.lastIndex;
      const precedingText = this.chatModel.content.substring(lastIndex, codeStartIndex);
      if (precedingText.trim().length > 0) {
        segments.push({content: precedingText, isCode: false});
      }
      const codeContent = match[1];
      segments.push({content: codeContent, isCode: true});
      lastIndex = codeEndIndex;
    }

    const remainingText = this.chatModel.content.substring(lastIndex);
    if (remainingText.trim().length > 0) {
      segments.push({content: remainingText, isCode: false});
    }

    this.separatedSegments = segments;
  }

  copyCode(content: string) {
    navigator.clipboard.writeText(content);
    this.notification.create(
      'success',
      '复制成功','',{nzClass: 'rounded-5'}
    );
  }


  protected readonly GPTType = GPTType;

  autoShow(image: DallImage) {
    // console.log(image)
    if(!image) return "";
    if(image.url===undefined) return '';
    try{
      if(image.url==null){
        return `data:image/png;base64,${image.b64_json}`;
      }else{
        return image.url;
      }
    }catch (e){
      // console.log("error "+e)
      // console.log(image)
      // console.log(image.url)
      // console.log(this.chatModel?.dataId)
      // console.log(this.chatModel?.type)
      return ''
    }
  }

  debugLog(){
    console.log(this.chatModel)
    console.log(this.separatedSegments)
    console.log(this.type)
    console.log(this.imageList)

  }

  errors() {
    return this.imageList === undefined && this.chatModel?.finish === true;
  }

  loading() {
    return this.imageList === undefined && this.chatModel?.finish === false;
  }
}
