import {Component, Input} from '@angular/core';
import {ChatModel, DallImage, ImageList} from "../../../models";
import {NzImageModule} from "ng-zorro-antd/image";
import {ChatModule} from "../../chat.module";
import {NzSpinModule} from "ng-zorro-antd/spin";
import {NgForOf, NgIf} from "@angular/common";
import {MarkdownRootComponent} from "../markdown-root/markdown-root.component";
@Component({
  selector: 'app-image',
  templateUrl: './image.component.html',
  styleUrl: './image.component.css',
  imports: [
    NzImageModule,
    NzSpinModule,
    NgIf,
    NgForOf,
    MarkdownRootComponent
  ],
  standalone: true
})
export class ImageComponent {
  private _chatModel: ChatModel | undefined;
  public imageList: ImageList | undefined;
  time: number = 0;
  pending: boolean = true;
  private timerInterval: any;
  constructor() {
    this.startTimer();
  }
  @Input()
  set content(value: number | undefined) {
    this.segmentsImage();
    if (value === undefined || value===0) {

    } else {
      this.stopTimer();
    }
  }

  @Input()
  set chatModel(value: ChatModel | undefined) {
    this._chatModel = value;
    this.segmentsImage();
  }

  get chatModel(): ChatModel | undefined {
    return this._chatModel;
  }
  fallback =
    'data:image/png;base64,';
  segmentsImage(){
    try{
      this.imageList = {
        dataId: this.chatModel?.dataId!,
        images: JSON.parse(this.chatModel?.content!) as DallImage[]
      }
    }catch (e){

    }
  }
  autoShow(image: DallImage) {
    if(!image) return "";
    if(image.url===undefined) return '';
    try{
      if(image.url==null){
        return `data:image/png;base64,${image.b64_json}`;
      }else{
        return image.url;
      }
    }catch (e){
      return ''
    }
  }
  errors() {
    return this.imageList === undefined && this.chatModel?.finish === true;
  }

  loading() {
    return this.imageList === undefined && this.chatModel?.finish === false;
  }
  delta = 200;
  startTimer() {
    this.timerInterval = setInterval(() => {
      this.time++;
      if(this.time>this.delta){
        clearInterval(this.timerInterval);
      }
    },100);
  }

  stopTimer() {
    clearInterval(this.timerInterval);
    this.pending = false;
  }

  getPendingText() {
    return `Already waiting ${this.time/10}s, please wait patiently`;
  }
}
